mapper.select:
SELECT user_id, user_password, user_name
FROM users
WHERE user_id=#{userId} AND user_password=#{userPassword}

SELECT user_id, user_password, user_name
FROM users
WHERE user_id=#{userId}

mapper.update:
UPDATE users
SET user_id = #{userId},
    user_password = #{userPassword},
    user_name = #{userName}
WHERE user_id = #{userId}
